import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import { toast } from "react-toastify";
import {
  Button,
  Form,
  Modal,
  Typography,
  Popconfirm,
  Input,
  Space,
  Spin,
  Image,
  DatePicker,
  Upload,
} from "antd";
import { ExclamationCircleFilled } from "@ant-design/icons";
import moment from "moment";
import { Table } from "antd";

import { useNavigate } from "react-router-dom";

import {
  deleteDriverListAction,
  getDriverListAction,
  updateDriverListAction,
} from "../action/DriverListAction";
import {
  clearDriverListDeleted,
  clearDriverListError,
  clearDriverListUpdated,
} from "../slices/DriverSlice";
const EditableCell = ({
  editing,
  dataIndex,
  title,
  inputType,
  record,
  index,
  children,
  ...restProps
}) => {
  let inputNode;
  const getFile = (e) => {
    if (Array.isArray(e)) {
      return e;
    }
    console.log(e);
    return e?.fileList[0]?.originFileObj;
  };
  const dummyRequest = ({ file, onSuccess }) => {
    setTimeout(() => {
      onSuccess("ok");
    }, 0);
  };
  inputNode =
    inputType === "textarea" ? (
      <>
        <Input.TextArea />
      </>
    ) : inputType === "file" ? (
      <>
        <Upload customRequest={dummyRequest}>
          <Button>Upload</Button>
        </Upload>
      </>
    ) : (
      <Input />
    );

  return (
    <td {...restProps}>
      {editing ? (
        <Form.Item
          name={dataIndex}
          style={{
            margin: 0,
          }}
          getValueFromEvent={getFile}
        >
          {/* <Input /> */}
          {inputNode}
        </Form.Item>
      ) : (
        children
      )}
    </td>
  );
};

const DriverList = () => {
  const [bordered, setBordered] = useState(true);
  const [tabledata, setTabledata] = useState([]);
  const [editingKey, setEditingKey] = useState("");
  const [form] = Form.useForm();
  const { confirm } = Modal;
  const dispatch = useDispatch();
  const tableProps = {
    bordered,
  };
  const {
    driver_detail = [],
    driverListLoading,
    isDriverListUpdated,
    isDriverListDeleted,
    error,
  } = useSelector((state) => state.DriverState || []);

  useEffect(() => {
    dispatch(getDriverListAction);
  }, []);
  useEffect(() => {
    if (driver_detail && driver_detail.length > 0) {
      let updatedDriver = driver_detail.map((item) => {
        const Created = moment
          .utc(item.createdAt)
          .local()
          .format("DD-MM-YYYY hh:mm A");
        const Updated = moment
          .utc(item.updatedAt)
          .local()
          .format("DD-MM-YYYY hh:mm A");

        return {
          ...item,
          Created: Created,
          Updated: Updated,
        };
      });

      setTabledata(updatedDriver);
    } else {
      setTabledata([]);
    }
  }, [driver_detail]);

  const isEditing = (record) => record._id === editingKey;
  const edit = (record) => {
    form.setFieldsValue({
      driverphoto: "",
      driver_pcc_file_upload: "",
      driver_badgefile_upload: "",
      driver_bgv_file_upload: "",
      driver_medical_insuranceno: "",
      driver_licence_exp: "",
      driver_licence_no: "",
      driver_name: "",
      driver_pcc_application_no: "",
      driver_pcc_exp: "",
      driver_aadhar_no: "",
      driver_badge_exp: "",
      driver_badge_no: "",
      driver_contact_no: "",
      driver_address: "",
      driver_dob: "",
      driver_insurance_no: "",
      driverphoto: "",
      driver_pcc_file_upload: "",
      driver_badgefile_upload: "",
      driver_bgv_file_upload: "",
      ...record,
    });
    setEditingKey(record._id);
  };
  const cancel = () => {
    setEditingKey("");
  };

  const columns = [
    {
      title: "Driver_Name",
      dataIndex: "driver_name",
      editable: true,
      fixed: "left",
    },

    {
      title: "Address",
      dataIndex: "driver_address",
      editable: true,
    },
    {
      title: "DOB",
      dataIndex: "driver_dob",
      editable: true,
    },
    {
      title: "Ph_No",
      dataIndex: "driver_contact_no",
      editable: true,
    },
    {
      title: "Aadhar",
      dataIndex: "driver_aadhar_no",
      editable: true,
    },
    {
      title: "Insurance_No",
      dataIndex: "driver_insurance_no",
      editable: true,
    },
    {
      title: "Medical_Insurance",
      dataIndex: "driver_medical_insuranceno",
      editable: true,
    },
    {
      title: "LIC_No",
      dataIndex: "driver_licence_no",
      editable: true,
    },
    {
      title: "Driver_Photo",
      dataIndex: "driverphoto",
      maxWidth: 50,
      render: (t, r) => (
        <Image
          src={r.driverphoto ? r.driverphoto : error}
          width={50}
          height={50}
          alt="Photo"
          fallback="data:image/png;base64,"
        />
      ),
      editable: true,
    },
    {
      title: "LIC_Exp",
      dataIndex: "driver_licence_exp",
      editable: true,
    },
    {
      title: "Badge_No",
      dataIndex: "driver_badge_no",
      editable: true,
    },
    {
      title: "Badge_Exp",
      dataIndex: "driver_badge_exp",
      editable: true,
    },
    {
      title: "Badge_File",
      dataIndex: "driver_badgefile_upload",
      maxWidth: 50,
      render: (t, r) => (
        <Image
          src={r.driver_badgefile_upload ? r.driver_badgefile_upload : error}
          width={50}
          height={50}
          alt="Badge"
          fallback="data:image/png;base64,"
        />
      ),
      editable: true,
    },

    {
      title: "Pcc_App_No",
      dataIndex: "driver_pcc_application_no",
      editable: true,
    },
    {
      title: "Pcc_Exp",
      dataIndex: "driver_pcc_exp",
      editable: true,
    },
    {
      title: "Pcc_File",
      dataIndex: "driver_pcc_file_upload",
      maxWidth: 50,
      render: (t, r) => (
        <Image
          src={r.driver_pcc_file_upload ? r.driver_pcc_file_upload : error}
          width={50}
          height={50}
          alt="PCC"
          fallback="data:image/png;base64,"
        />
      ),
      editable: true,
    },

    {
      title: "Bgv_File",
      dataIndex: "driver_bgv_file_upload",
      maxWidth: 50,
      render: (t, r) => (
        <Image
          src={r.driver_bgv_file_upload ? r.driver_bgv_file_upload : error}
          width={50}
          height={50}
          alt="BGV"
          fallback="data:image/png;base64,"
        />
      ),
      editable: true,
    },

    {
      title: "Action",
      dataIndex: "",
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Space>
            <Typography.Link
              onClick={() => saveDriver(record._id)}
              style={{
                marginRight: 8,
              }}
            >
              Save
            </Typography.Link>
            <Popconfirm title="Sure to cancel?" onConfirm={cancel}>
              <a>Cancel</a>
            </Popconfirm>
          </Space>
        ) : (
          <Space>
            {/* <Typography.Link
              disabled={editingKey !== ""}
              onClick={() => edit(record)}
            >
              <Button>Edit</Button>
            </Typography.Link> */}
            <Button
              className="bg-red-700 hover:bg-red-400  border-red-700 hover:border-red-500 text-white"
              onClick={() => deleteDriverFunc(record?._id)}
            >
              Delete
            </Button>
          </Space>
        );
      },
    },
  ];
  const mergedColumns = columns.map((col) => {
    if (!col.editable) {
      return col;
    }
    return {
      ...col,
      onCell: (record) => ({
        record,
        inputType:
          col.dataIndex === "driver_address"
            ? "textarea"
            : col.dataIndex === "driver_licence_exp" ||
              col.dataIndex === "driver_pcc_exp" ||
              col.dataIndex === "driver_badge_exp" ||
              col.dataIndex === "driver_dob"
            ? "date"
            : col.dataIndex === "driverphoto" ||
              col.dataIndex === "driver_pcc_file_upload" ||
              col.dataIndex === "driver_badgefile_upload" ||
              col.dataIndex === "driver_bgv_file_upload"
            ? "file"
            : "text",
        dataIndex: col.dataIndex,
        title: col.title,
        editing: isEditing(record),
      }),
    };
  });
  const saveDriver = async (key) => {
    try {
      const row = await form.validateFields();
      console.log(row);
      dispatch(
        updateDriverListAction(key, {
          driverphoto: row.driverphoto,
          driver_pcc_file_upload: row.driver_pcc_file_upload,
          driver_badgefile_upload: row.driver_badgefile_upload,
          driver_bgv_file_upload: row.driver_bgv_file_upload,
          driver_medical_insuranceno: row.driver_medical_insuranceno,
          driver_licence_exp: row.driver_licence_exp,
          driver_licence_no: row.driver_licence_no,
          driver_name: row.driver_name,
          driver_pcc_application_no: row.driver_pcc_application_no,
          driver_pcc_exp: row.driver_pcc_exp,
          driver_aadhar_no: row.driver_aadhar_no,
          driver_badge_exp: row.driver_badge_exp,
          driver_badge_no: row.driver_badge_no,
          driver_contact_no: row.driver_contact_no,
          driver_address: row.driver_address,
          driver_dob: row.driver_dob,
          driver_insurance_no: row.driver_insurance_no,
          driverphoto: row.driverphoto,
          driver_pcc_file_upload: row.driver_pcc_file_upload,
          driver_badgefile_upload: row.driver_badgefile_upload,
          driver_bgv_file_upload: row.driver_bgv_file_upload,
        })
      );
      // dispatch(editAreaAction(key, { Area: row.value }));
      setEditingKey("");
    } catch (errInfo) {
      alert("Validate Failed:", errInfo);
    }
  };
  const deleteDriverFunc = (id) => {
    // setCurrentPage(1);
    confirm({
      title: "Do you want to Delete these Driver Details?",
      icon: <ExclamationCircleFilled />,

      onOk() {
        dispatch(deleteDriverListAction(id));
      },
      onCancel() {
        console.log("Cancel");
      },
    });
  };
  useEffect(() => {
    if (isDriverListUpdated) {
      toast("Driver Updated Succesfully!", {
        type: "success",
        position: toast.POSITION.BOTTOM_CENTER,
        onOpen: () => dispatch(clearDriverListUpdated()),
      });

      return;
    }
    if (isDriverListDeleted) {
      toast("Driver Deleted Succesfully!", {
        type: "success",
        position: toast.POSITION.TOP_CENTER,
        onOpen: () => dispatch(clearDriverListDeleted()),
      });
      return;
    }

    if (error) {
      toast(error, {
        position: toast.POSITION.BOTTOM_CENTER,
        type: "error",
        onOpen: () => {
          dispatch(clearDriverListError());
        },
      });
      return;
    }
  }, [isDriverListUpdated, isDriverListDeleted, error, dispatch]);
  const navigate = useNavigate();
  const CreateNewDriver = () => {
    navigate("/driver/new_driver");
  };
  const { Title } = Typography;
  return (
    <>
      <Spin spinning={driverListLoading} tip="loading">
        <div class="flex flex-row items-center justify-between mt-5">
          <Title className="font-bold" level={4}>
            Driver List
          </Title>
          <Button
            className="text-white border-green-500 bg-green-500 hover:bg-white mb-7"
            onClick={CreateNewDriver}
          >
            New Driver
          </Button>
        </div>
        <Form form={form} component={false}>
          <Table
            id={"driver_detail"}
            {...tableProps}
            columns={mergedColumns}
            dataSource={tabledata}
            scroll={{
              y: 900,
              x: 2500,
            }}
            components={{
              body: {
                cell: EditableCell,
              },
            }}
            pagination={{
              onChange: cancel,
            }}
          />
        </Form>
      </Spin>
    </>
  );
};

export default DriverList;
